import * as express from 'express';
import loginRoute from './routes/loginRoute';
import teamsRoute from './routes/teamsRoute';
import matchesRoute from './routes/matchesRoute';
import leaderboardRoute from './routes/leaderboardRoute';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();

    this.config();

    // Não remover essa rota
    this.app.get('/', (req, res) => res.json({ ok: true }));
  }

  private config():void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(express.json());
    this.app.use(accessControl);
    this.app.use('/login', loginRoute);
    this.app.use('/teams', teamsRoute);
    this.app.use('/matches', matchesRoute);
    this.app.use('/leaderboard', leaderboardRoute);
  }

  public start(PORT: string | number):void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export { App };

// Essa segunda exportação é estratégica, e a execução dos testes de cobertura depende dela
export const { app } = new App();

import { Router } from 'express';
import loginValidate from '../middlewares/validateLogin';
import validaToken from '../middlewares/validaToken';
import usersController from '../controllers/usersController';

const routers = Router();

routers.get('/validate', validaToken, usersController.userRole);
routers.post('/', loginValidate, usersController.findUser);

export default routers;

import { Request, Response, NextFunction } from 'express';

const validateLogin = (req: Request, res: Response, next: NextFunction) => {
  const { email, password } = req.body;
  if (!email || !password) {
    return res.status(400).json({ message: 'All fields must be filled' });
  }
  const isValidEmail = email.match(/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g);
  if (!isValidEmail) {
    return res.status(401).json({ message: 'Incorrect email or password' });
  }
  next();
};

export default validateLogin;

import { Request, Response, NextFunction } from 'express';
import token from '../utils/token';

const authToken = (req: Request, res: Response, next: NextFunction) => {
  const { authorization } = req.headers;

  if (!authorization) {
    return res.status(400).json({ message: 'Token not found' });
  }

  const decoded = token.verifyToken(authorization);

  req.body.user = decoded;

  next();
};

export default authToken;

import * as jwt from 'jsonwebtoken';
import IUser from '../interfaces/IUser';

const secret = 'jwt_secret';

const generateToken = (data: IUser) => {
  const token = jwt.sign(data, secret, { algorithm: 'HS256', expiresIn: '360min' });
  return token;
};

const verifyToken = (token: string) => {
  const decoded = jwt.verify(token, secret);
  return decoded;
};

export default {
  generateToken,
  verifyToken,
};

import { Request, Response } from 'express';
import usersService from '../services/userService';

const findUser = async (req: Request, res: Response):Promise<void> => {
  const { email, password } = req.body;
  const { status, message } = await usersService.findUser(email, password);
  if (status === 200) {
    res.status(status).json({ token: message });
  } else res.status(status).json({ message });
};

const userRole = async (req: Request, res: Response) => {
  const { role } = req.body.user;
  res.status(200).json({ role });
};

export default {
  findUser,
  userRole,
};

import * as bcrypt from 'bcryptjs';
import IResponse from '../interfaces/IResponse';
import usersModel from '../database/models/usersModel';
import token from '../utils/token';

const findUser = async (email: string, password: string): Promise<IResponse> => {
  const userFound = await usersModel.findOne({ where: { email } });
  if (!userFound) {
    return { status: 401, message: 'Incorrect email or password' };
  }
  const checkPassword = await bcrypt.compare(password, userFound.dataValues.password);
  if (checkPassword === false) {
    return { status: 401, message: 'Incorrect email or password' };
  }
  const userToken = await token.generateToken(userFound.dataValues);
  return { status: 200, message: userToken };
};

export default {
  findUser,
};

