import { Router } from 'express';
import matchesController from '../controllers/matchesController';

const routers = Router();

routers.patch('/:id/finish', matchesController.finishMatch);
routers.patch('/:id', matchesController.updateMatch);
routers.get('/', matchesController.getMatches);
routers.post('/', matchesController.createMatch);

export default routers;

import { Request, Response } from 'express';
import matchesService from '../services/matchesService';
import token from '../utils/token';

const getMatches = async (req: Request, res: Response):Promise<void> => {
  const { inProgress } = req.query;
  if (inProgress) {
    const booleanConvert: boolean = [inProgress].includes('true');
    const { status, matchesFound } = await matchesService.getMatchesInProgress(booleanConvert);
    res.status(status).json(matchesFound);
  } else {
    const { status, matchesFound } = await matchesService.getMatches();
    res.status(status).json(matchesFound);
  }
};

const createMatch = async (req: Request, res: Response):Promise<void> => {
  const { body, headers } = req;
  if (headers.authorization) {
    try {
      token.verifyToken(headers.authorization);
      if (body.homeTeamId === body.awayTeamId) {
        res.status(422)
          .json({ message: 'It is not possible to create a match with two equal teams' });
      } else {
        const { status, matchCreated } = await matchesService.createMatch(body);
        if (status === 404) res.status(status).json({ message: 'There is no team with such id!' });
        else res.status(status).json(matchCreated);
      }
    } catch {
      res.status(401).json({ message: 'Token must be a valid token' });
    }
  } else {
    res.status(401).json({ message: 'Token must be a valid token' });
  }
};

const finishMatch = async (req: Request, res: Response):Promise<void> => {
  const { id } = req.params;
  const { status, message } = await matchesService.finishMatch(id);
  res.status(status).json({ message });
};

const updateMatch = async (req: Request, res: Response):Promise<void> => {
  const { id } = req.params;
  const { homeTeamGoals, awayTeamGoals } = req.body;
  const { status, message } = await matchesService.updateMatch(id, homeTeamGoals, awayTeamGoals);
  res.status(status).json({ message });
};

export default {
  getMatches,
  createMatch,
  finishMatch,
  updateMatch,
};

import matchModel from '../database/models/matchModel';
import teamsService from './teamsService';
import { IMatch, ICreateMatch } from '../interfaces/IMatch';

const getTeamName = async (match: IMatch) => {
  const homeTeamName = await teamsService.findTeam(match.homeTeam);
  const awayTeamName = await teamsService.findTeam(match.awayTeam);
  return {
    id: match.id,
    homeTeamId: homeTeamName.teamFound?.dataValues.id,
    homeTeamGoals: match.homeTeamGoals,
    awayTeamId: awayTeamName.teamFound?.dataValues.id,
    awayTeamGoals: match.awayTeamGoals,
    inProgress: match.inProgress,
    homeTeam: { teamName: homeTeamName.teamFound?.dataValues.teamName },
    awayTeam: { teamName: awayTeamName.teamFound?.dataValues.teamName },
  };
};

const getMatches = async () => {
  const result = await matchModel.findAll();
  const matchesFound = await Promise.all(result.map((e) => getTeamName(e.dataValues)));
  return { status: 200, matchesFound };
};

const getMatchesInProgress = async (progress: boolean) => {
  const result = await matchModel.findAll({ where: { inProgress: progress } });
  const matchesFound = await Promise.all(result.map((e) => getTeamName(e.dataValues)));
  return { status: 200, matchesFound };
};

const checkTeamsId = async (homeTeamId:number, awayTeamId:number) => {
  const homeTeam = await teamsService.findTeam(homeTeamId);
  const awayTeam = await teamsService.findTeam(awayTeamId);
  return !(homeTeam.status !== 200 || awayTeam.status !== 200);
};

const createMatch = async (match: ICreateMatch) => {
  const check = await checkTeamsId(match.homeTeamId, match.awayTeamId);
  if (check === false) return { status: 404 };
  const result = await matchModel.create({
    homeTeam: match.homeTeamId,
    homeTeamGoals: match.homeTeamGoals,
    awayTeam: match.awayTeamId,
    awayTeamGoals: match.awayTeamGoals,
    inProgress: true,
  });
  const matchCreated = {
    id: result.dataValues.id,
    homeTeamId: result.dataValues.homeTeam,
    homeTeamGoals: result.dataValues.homeTeamGoals,
    awayTeamId: result.dataValues.awayTeam,
    awayTeamGoals: result.dataValues.awayTeamGoals,
    inProgress: result.dataValues.inProgress,
  };
  return { status: 201, matchCreated };
};

const finishMatch = async (id: string) => {
  await matchModel.update({ inProgress: false }, { where: { id } });
  return { status: 200, message: 'Finished' };
};

const updateMatch = async (id: string, homeTeamGoals: number, awayTeamGoals: number) => {
  await matchModel.update({ homeTeamGoals, awayTeamGoals }, { where: { id } });
  return { status: 200, message: 'Updated' };
};

export default {
  getMatches,
  getMatchesInProgress,
  createMatch,
  finishMatch,
  updateMatch,
};

import { Model, BOOLEAN, INTEGER } from 'sequelize';
import db from '.';
import Team from './teamsModel';

class Match extends Model {
  declare id: number;
  declare homeTeam: number;
  declare homeTeamGoals: number;
  declare awayTeam: number;
  declare awayTeamGoals: number;
  declare inProgress: boolean;
}

Match.init({
  id: {
    type: INTEGER,
    allowNull: false,
    primaryKey: true,
    autoIncrement: true,
  },
  homeTeam: {
    type: INTEGER,
    allowNull: false,
    field: 'home_team_id',
  },
  homeTeamGoals: {
    type: INTEGER,
    allowNull: false,
    field: 'home_team_goals',
  },
  awayTeam: {
    type: INTEGER,
    allowNull: false,
    field: 'away_team_id',
  },
  awayTeamGoals: {
    field: 'away_team_goals',
    allowNull: false,
    type: INTEGER,
  },
  inProgress: {
    field: 'in_progress',
    allowNull: false,
    type: BOOLEAN,
  },
}, {
  underscored: true,
  sequelize: db,
  modelName: 'matches',
  timestamps: false,
});

Match.hasMany(Team, { foreignKey: 'id', as: 'teamHome' });
Match.hasMany(Team, { foreignKey: 'id', as: 'teamAway' });

Team.belongsTo(Match, { foreignKey: 'id', as: 'teamHome' });
Team.belongsTo(Match, { foreignKey: 'id', as: 'teamAway' });

export default Match;

import { Router } from 'express';
import leaderboardController from '../controllers/leaderboardController';

const routers = Router();

routers.get('/home', leaderboardController.getLeaderboard);
routers.get('/away', leaderboardController.getLeaderboard);
routers.get('/', leaderboardController.getGeneralLeaderboard);

export default routers;

import { Request, Response } from 'express';
import { IFullMatch, ITeamMatch } from '../interfaces/IMatch';
import matchesService from '../services/matchesService';
import teamsService from '../services/teamsService';

const allPoints = (totalPoints: number, match: IFullMatch, id: number) => {
  if (id === match.homeTeamId) {
    if (match.homeTeamGoals > match.awayTeamGoals) return totalPoints + 3;
    if (match.homeTeamGoals < match.awayTeamGoals) return totalPoints;
    return totalPoints + 1;
  }
  if (match.homeTeamGoals < match.awayTeamGoals) return totalPoints + 3;
  if (match.homeTeamGoals > match.awayTeamGoals) return totalPoints;
  return totalPoints + 1;
};

const calculatePoints = (totalPoints: number, match: IFullMatch, query: string, id: number) => {
  if (query === 'general') {
    return allPoints(totalPoints, match, id);
  }
  if (query === 'home') {
    if (match.homeTeamGoals > match.awayTeamGoals) return totalPoints + 3;
    if (match.homeTeamGoals < match.awayTeamGoals) return totalPoints + 0;
    return totalPoints + 1;
  }
  if (match.homeTeamGoals < match.awayTeamGoals) return totalPoints + 3;
  if (match.homeTeamGoals > match.awayTeamGoals) return totalPoints + 0;
  return totalPoints + 1;
};

const calculateVictories = (teamMatchesFound: IFullMatch[], query: string, id: number): number => {
  if (query === 'general') {
    const homeVictory = teamMatchesFound
      .filter((e: IFullMatch) => e.homeTeamId === id && e.homeTeamGoals > e.awayTeamGoals).length;
    const awayVictory = teamMatchesFound
      .filter((e: IFullMatch) => e.awayTeamId === id && e.awayTeamGoals > e.homeTeamGoals).length;
    return homeVictory + awayVictory;
  } if (query === 'home') {
    return teamMatchesFound.filter((e: IFullMatch) => e.homeTeamGoals > e.awayTeamGoals).length;
  }
  return teamMatchesFound.filter((e: IFullMatch) => e.awayTeamGoals > e.homeTeamGoals).length;
};

const calculateDraws = (teamMatchesFound: IFullMatch[]): number => {
  const result = teamMatchesFound
    .filter((e: IFullMatch) => e.homeTeamGoals === e.awayTeamGoals).length;
  return result;
};

const calculateLosses = (teamMatchesFound: IFullMatch[], query: string, id: number) => {
  if (query === 'general') {
    const homeLosses = teamMatchesFound
      .filter((e: IFullMatch) => e.homeTeamId === id && e.homeTeamGoals < e.awayTeamGoals).length;
    const awayLosses = teamMatchesFound
      .filter((e: IFullMatch) => e.awayTeamId === id && e.awayTeamGoals < e.homeTeamGoals).length;
    return homeLosses + awayLosses;
  } if (query === 'home') {
    return teamMatchesFound.filter((e: IFullMatch) => e.homeTeamGoals < e.awayTeamGoals).length;
  }
  return teamMatchesFound.filter((e: IFullMatch) => e.awayTeamGoals < e.homeTeamGoals).length;
};

const calculateGoalsFavor = (match: IFullMatch, id: number) => {
  if (id === match.homeTeamId) {
    return match.homeTeamGoals;
  }
  return match.awayTeamGoals;
};

const calculateGoalsOwn = (match: IFullMatch, id: number) => {
  if (id === match.homeTeamId) {
    return match.awayTeamGoals;
  }
  return match.homeTeamGoals;
};

const calculateTeamStats = (teamMatchesFound: IFullMatch[], query: string, id: number) => {
  const teamStats = {
    totalPoints: 0,
    totalGames: teamMatchesFound.length,
    totalVictories: calculateVictories(teamMatchesFound, query, id),
    totalDraws: calculateDraws(teamMatchesFound),
    totalLosses: calculateLosses(teamMatchesFound, query, id),
    goalsFavor: 0,
    goalsOwn: 0,
  };
  teamMatchesFound.map((match: IFullMatch) => {
    const points = calculatePoints(teamStats.totalPoints, match, query, id);
    teamStats.totalPoints = points;
    teamStats.goalsFavor += calculateGoalsFavor(match, id);
    teamStats.goalsOwn += calculateGoalsOwn(match, id);
    return true;
  });
  return teamStats;
};

const calculateEfficiency = (matches: number, points: number) => {
  const efficiency = ((points * 100) / (matches * 3)).toFixed(2);
  return efficiency;
};

const getTeamMatches = async (id: number, query: string) => {
  const { matchesFound } = await matchesService.getMatches();
  let teamMatches = [];
  if (query === 'general') {
    teamMatches = matchesFound
      .filter((e) => (e.homeTeamId === id || e.awayTeamId === id) && e.inProgress === false);
  } else if (query === 'home') {
    teamMatches = matchesFound.filter((e) => e.homeTeamId === id && e.inProgress === false);
  } else {
    teamMatches = matchesFound.filter((e) => e.awayTeamId === id && e.inProgress === false);
  }
  const teamStats = calculateTeamStats(teamMatches, query, id);
  const { teamFound } = await teamsService.findTeam(id);
  return {
    name: teamFound?.teamName,
    ...teamStats,
    goalsBalance: teamStats.goalsFavor - teamStats.goalsOwn,
    efficiency: calculateEfficiency(teamMatches.length, teamStats.totalPoints),
  };
};

const getTeamMatchesTeste = async (id: number, query: string, matchesFound: IFullMatch[]) => {
  let teamMatches = [];
  if (query === 'general') {
    teamMatches = matchesFound
      .filter((e) => (e.homeTeamId === id || e.awayTeamId === id) && e.inProgress === false);
  } else if (query === 'home') {
    teamMatches = matchesFound.filter((e) => e.homeTeamId === id && e.inProgress === false);
  } else {
    teamMatches = matchesFound.filter((e) => e.awayTeamId === id && e.inProgress === false);
  }
  const teamStats = calculateTeamStats(teamMatches, query, id);
  const { teamFound } = await teamsService.findTeam(id);
  return {
    name: teamFound?.teamName,
    ...teamStats,
    goalsBalance: teamStats.goalsFavor - teamStats.goalsOwn,
    efficiency: calculateEfficiency(teamMatches.length, teamStats.totalPoints),
  };
};

const sortLeaderboard = (teamMatches: ITeamMatch[]) => teamMatches
  .sort((a: ITeamMatch, b: ITeamMatch) => (
    b.totalPoints - a.totalPoints
    || b.totalVictories - a.totalVictories
    || b.goalsBalance - a.goalsBalance
    || b.goalsFavor - a.goalsFavor
    || a.goalsOwn - b.goalsOwn
  ));

const getLeaderboard = async (req: Request, res: Response): Promise<void> => {
  const { path } = req.route;
  const query = path.substring(1);
  const { teamsFound } = await teamsService.getTeams();
  const teamsId = teamsFound.map((e) => e.id);
  const teamMatches = await Promise.all(teamsId.map((team) => getTeamMatches(team, query)));
  const pointsOrder = sortLeaderboard(teamMatches);
  res.status(200).json(pointsOrder);
};

const getGeneralLeaderboard = async (req: Request, res: Response): Promise<void> => {
  const { teamsFound } = await teamsService.getTeams();
  const teamsId = teamsFound.map((e) => e.id);
  const { matchesFound } = await matchesService.getMatches();
  const teamMatches = await Promise
    .all(teamsId.map((team) => getTeamMatchesTeste(team, 'general', matchesFound)));
  const pointsOrder = sortLeaderboard(teamMatches);
  res.status(200).json(pointsOrder);
};

export default {
  getLeaderboard,
  getGeneralLeaderboard,
};
